def translate(txt):
    start = time.time()
    for _ in range(3):
        try:
            rst = openai.ChatCompletion.create(
              model="gpt-3.5-turbo",
              messages=[
                    {"role": "user", "content": f'将英文准确翻译成中文："{txt}"'},
                ]
            )
            break
        except:
            traceback.print_exc()
            time.sleep(10)
    else:
        raise Exception()

    end = time.time()

    if end - start < frame_sec:
        time.sleep(frame_sec - end + start + 0.1)

    return rst['choices'][0]['message']['content'].strip()
